import { useState, useEffect, useLayoutEffect } from "react";
import React from "react";

const ethers = require("ethers");
const utils = require("ethers").utils;

class ConnectionScreent extends React.Component {
  ethereum = window.ethereum;

  constructor() {
    super();
    this.ConnectWithMetamask();
  }

  ConnectWithMetamask() {
    if (ethereum) {
      ethereum.request({ method: "eth_requestAccounts" }).then((result) => {
        this.props.setAccount(result[0]);
        this.props.setConfirmation(true);

        GetBalanceUser(result[0]);
      });
    } else {
      alert("No connection found");
    }
  }

  GetBalanceUser(address) {
    ethereum
      .request({ method: "eth_getBalance", params: [address, "latest"] })
      .then((result) => {
        console.log(result);
        this.props.setBalance(result);
      });
  }

  render() {
    return (
      <div class="container w-50 my-5">
        <div class="card">
          <img
            class="card-img-top img-responsive"
            src="lock-icon.png"
            alt="Card image cap"
          ></img>
          <div class="card-body">
            <button class="btn btn-primary" onClick={ConnectWithMetamask}>
              {" "}
              Connect Metamask{" "}
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default ConnectionScreent;
