import { useState } from "react";

const ethers = require("ethers");
const utils = require("ethers").utils;

function CreateTx(props) {
  const [address, setAddress] = useState("");
  const [amount, setAmount] = useState(0);

  function SetAddress(event) {
    setAddress(event.target.value);
  }

  function SetAmount(event) {
    setAmount(event.target.value);
  }

  const contractAbi = [
    "function getId() view returns(uint256)",
    "function submit(address recipient, uint256 value, bytes calldata data)",
    "function approve(uint txId)",
    "function execute(uint txId)",
    "function revokeApproval(uint txId)",
  ];

  const addressContract = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

  async function SubmitTransaction() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(addressContract, contractAbi, signer);

    try {
      const submission = await contract.submit(address, amount, "0x00");
      if (submission) {
        //props.setNewTxSubmited();
      }
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div class="card my-4">
      <div class="card-header">
        <h4 class="text-dark">Create Tx</h4>
      </div>
      <div class="card-body">
        <form>
          <div class="form-group">
            <h5 class="form-label text-start">Address</h5>
            <input
              onChange={SetAddress}
              class="form-control w-75"
              disabled={props.trigger}
            ></input>
          </div>
          <div class="form-group my-4">
            <h5 class="form-label text-start">Amount</h5>
            <input
              onChange={SetAmount}
              class="form-control w-50"
              disabled={props.trigger}
            ></input>
          </div>
          <div class="d-flex">
            <button onClick={SubmitTransaction} class="btn btn-primary my-2">
              {" "}
              Submit Tx
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default CreateTx;
