import { useEffect, useState } from "react";
import { addressContract, contractAbi } from "./CreatingWebConnection.js";

const ethers = require("ethers");
const utils = require("ethers").utils;

function ManageTx(props) {
  const [addressSender, setAddressSender] = useState("0x00");
  const [value, setValue] = useState(0);
  const [executed, setExecuted] = useState(false);
  const [numberOfConfirmations, setConfirmations] = useState(0);

  useEffect(() => {
    GetContractDetailes();
  }, []);

  async function GetContractDetailes() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(addressContract, contractAbi, signer);

    const { address, value, executed, numberOfConfirmations } =
      await contract.callStatic.getLastTx();

    setAddressSender(address);
    setValue(value);
    setExecuted(executed);
    setConfirmations(numberOfConfirmations);
  }

  return (
    <div class="card my-4">
      <div class="card-header">
        <h4 class="text-dark">Manage Tx</h4>
      </div>
      <div class="card-body">
        <div>
          <h5 class="form-label text-start">Address: {addressSender}</h5>
        </div>
        <div class="my-4">
          <h5 class="form-label text-start">Amount: {value}</h5>
        </div>
        <div class="my-4">
          <h5 class="form-label text-start">
            Confirmations {numberOfConfirmations}
          </h5>
        </div>
        <div class="d-flex">
          <button class="btn btn-primary my-2"> Approve Tx</button>
          <button class="btn btn-danger my-2 mx-3"> Revoke Tx</button>
        </div>
      </div>
    </div>
  );
}

export default ManageTx;
