import "./App.css";
import { useState } from "react";
import "bootstrap/dist/css/bootstrap.css";
import NavBar from "./NavBar";
import CreateTx from "./CreateTx";
import ManageTx from "./ManageTx";
import ConnectionScreent from "./ConnectionScreen";

const ethers = require("ethers");
const utils = require("ethers").utils;

function App(props) {
  // details about the account
  const [account, setAccount] = useState("0x0");
  const [balance, setBalance] = useState(0);
  const [confirmation, setConfirmation] = useState(false);

  const ethereum = window.ethereum;

  async function FundContract() {
    // const contractAbi = [
    //   "function getId() view returns(uint256)",
    //   "function submit(address recipient, uint256 value, bytes calldata data)",
    //   "function approve(uint txId)",
    //   "function execute(uint txId)",
    //   "function revokeApproval(uint txId)",
    // ];

    const addressContract = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

    if (ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      // const contract = new ethers.Contract(
      //   addressContract,
      //   contractAbi,
      //   signer
      // )

      try {
        const tx = {
          to: addressContract,
          value: utils.parseEther("3.0"),
          chainId: "31337",
        };
        const confirmationTx = await signer.sendTransaction(tx);

        if (confirmationTx) {
          setConfirmation(true);
        }
      } catch (error) {
        console.log("ERRROR");
      }
    }
  }

  const [addressToSend, ChangeAddress] = useState("");
  const [amountToSend, ChangeAmount] = useState(0);

  async function SubmitTransaction() {
    const contractAbi = [
      "function getId() view returns(uint256)",
      "function submit(address recipient, uint256 value, bytes calldata data)",
      "function approve(uint txId)",
      "function execute(uint txId)",
      "function revokeApproval(uint txId)",
    ];

    const addressContract = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(addressContract, contractAbi, signer);

    try {
      console.log(addressToSend);
      const submission = await contract.submit(
        addressToSend,
        amountToSend,
        "0x00"
      );
    } catch (error) {
      console.log(error);
    }
  }

  function submitAddress(value) {
    ChangeAddress(value.target.value);
  }

  function submitValue(value) {
    ChangeAmount(value.target.value);
  }

  return confirmation ? (
    <div className="App">
      <NavBar value={balance} />
      <div class="container my-4">
        <div class="row border">
          <div class="col-md-2 border">
            <h1>Last tX</h1>
          </div>
          <div class="col my-2">
            <div class="container-sm">
              <CreateTx trigger={false} />
              <ManageTx />
            </div>
          </div>
        </div>
      </div>

      {/* <h1> {props.somtheing} </h1>
      <button class="btn btn-primary my-2" onClick={ConnectWithMetamask}>
        {" "}
        Connect Wallet{" "}
      </button>
      <h2 class="container-md border"> {account} </h2>
      <h2> {balance} </h2>
      <button onClick={FundContract}> Fund Contract </button>
      <div class="card w-25 my-4">
        <h1 class="card-header"> Yolo </h1>
        <div class="card-body">
          Lorem Ipsum is simply dummy text of the printing and typesetting
          industry. Lorem Ipsum has been the industry's standard dummy text ever
          since the 1500s, when an unknown printer took a galley
        </div>
      </div>
      <div>
        <input onChange={submitAddress} placeholder="Enter Address" />
        <input onChange={submitValue} placeholder="Enter Amount" />
        <button onClick={SubmitTransaction}> SubmitTx </button>
      </div>

      <h2> {confirmation} </h2> */}
    </div>
  ) : (
    <div className="App">
      <NavBar value="1320" />
      <ConnectionScreent
        setAccount={setAccount}
        setConfirmation={setConfirmation}
        setBalance={setBalance}
      />
    </div>
  );
}

export default App;
